import React, { Component } from 'react';
import {connect} from 'react-redux';
import { bindActionCreators } from 'redux';
import * as actionCreators from '../../homepage-redux/actions';

class Homepage extends Component {
    
    titleChangeHandler= () => {
        this.props.actions.homeUpdate({
            title: 'Random Title dispatched'
        });
    }

    render() {
        return ( 
            <div> 
                <h1>{this.props.title}</h1>
                <button onClick={this.titleChangeHandler}>Change the title?</button>
            </div>
        );
    }
}

const mapStateToProps = ({home: {title}}) => ({
    title
});

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(actionCreators, dispatch)
});

export default connect(mapStateToProps, mapDispatchToProps)(Homepage);